import React, { Suspense, useRef, useEffect, useState, } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { OrbitControls, Stage, Html, useProgress } from "@react-three/drei";
import Model from "./Model";
import Header from "./components/Header"
import "./App.scss";
import { Section } from "./components/Section";
import { a, useTransition } from "@react-spring/web"
import state from './components/state'
import { useInView } from 'react-intersection-observer';

function myModel({ url }) {

  return <Model />
}

const Lights = () => {
  return (
    <>
      {/* Ambient Light illuminates lights for all objects */}
      <ambientLight intensity={0.3} />
      {/* Diretion light */}
      <directionalLight position={[10, 10, 5]} intensity={1} />
      <directionalLight
        castShadow
        position={[0, 10, 0]}
        intensity={1.5}
        shadow-mapSize-width={1024}
        shadow-mapSize-height={1024}
        shadow-camera-far={50}
        shadow-camera-left={-10}
        shadow-camera-right={10}
        shadow-camera-top={10}
        shadow-camera-bottom={-10}
      />
      {/* Spotlight Large overhead light */}
      <spotLight intensity={1} position={[1000, 0, 0]} castShadow />
    </>
  );
};

const HTMLContent = ({
  domContent,
  children,
  bgColor,
  modelPath,
  position,
}) => {
  const ref = useRef();
  // useFrame(() => (ref.current.rotation.y += 0.01));
  const [refItem, inView] = useInView({
    threshold: 0,
  });
  useEffect(() => {
    inView && (document.body.style.background = bgColor);
  }, [inView]);

  return (
    <Section factor={1.5} offset={1}>
      <group position={[0, position, 0]}>
        <Model />
        <Html fullscreen portal={domContent}>
          <div className='container'>
            <h1 className='title'>{children}</h1>
          </div>
        </Html>
      </group>
    </Section>
  );
};
function Loader() {
  const { active, progress } = useProgress();
  const transition = useTransition(active, {
    from: { opacity: 1, progress: 0 },
    leave: { opacity: 0 },
    update: { progress },
  });
  return transition(
    ({ progress, opacity }, active) =>
      active && (
        <a.div className='loading' style={{ opacity }}>
          <div className='loading-bar-container'>
            <a.div className='loading-bar' style={{ width: progress }}></a.div>
          </div>
        </a.div>
      )
  );
}



export default function Viewer() {
  const [events, setEvents] = useState();
  const domContent = useRef();
  const scrollArea = useRef();
  const onScroll = (e) => (state.top.current = e.target.scrollTop);
  useEffect(() => void onScroll({ target: scrollArea.current }), []);

  return (
   <>
    <Header />
    <Canvas shadows dpr={[1, 2]} camera={{position: [0, 0, 120], fov: 70 }}>
      <Suspense fallback={null}>
      <HTMLContent
          domContent={domContent}
          bgColor='#f15946'
          position={250}>
          <span>Meet the new </span>
          <span>shopping experience </span>
          <span>for online chairs</span>
        </HTMLContent>
        <HTMLContent
          domContent={domContent}
          bgColor='#571ec1'
          position={0}>
          <span>Shit... we even</span>
          <span>got different colors</span>
        </HTMLContent>
        <HTMLContent
          domContent={domContent}
          bgColor='#636567'
          position={-250}>
          <span>And yes</span>
          <span>we even got</span>
          <span>monochrome!</span>
        </HTMLContent>
        {/*  */}
        <Lights />










      </Suspense>
      {/* <OrbitControls useRef={ref} autoRotate enableZoom={false} enablePan={false} enableRotate={false}  /> */}
    </Canvas>
    <Loader />
    <div
      className='scrollArea'
      ref={scrollArea}
      onScroll={onScroll}
      {...events}>
      <div style={{ position: "sticky", top: 0 }} ref={domContent} />
      <div style={{ height: `${state.pages * 100}vh` }} />
    </div>
   </>
  );
}
